mapper.insert:
insert into plugin_packages (id, name, version, 
  status, upload_timestamp, ui_package_included
  )
values (#{id,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, #{version,jdbcType=VARCHAR}, 
  #{status,jdbcType=VARCHAR}, #{uploadTimestamp,jdbcType=TIMESTAMP}, #{uiPackageIncluded,jdbcType=BIT}
  )

mapper.select:
select 

from plugin_packages
where name = #{name,jdbcType=VARCHAR}
and version = #{version,jdbcType=VARCHAR}
