mapper.select:
select
,
from core_re_proc_def_info where active = false and ( status = 'deployed' or status = 'draft' )

select
,
from core_re_proc_def_info where proc_def_key = #{procDefKey,jdbcType=VARCHAR} and active = true and is_deleted = false and status = #{status,jdbcType=VARCHAR}

SELECT t1.id , t1.proc_def_key AS procDefKey, t1.proc_def_name AS procDefName, t1.root_entity AS rootEntity, t1.status AS status, t1.proc_def_ver AS procDefVersion FROM core_re_proc_def_info t1 , core_ru_proc_role_binding t2 WHERE t2.proc_id = t1.id AND t2.permission = 'USE' AND t2.role_name IN
#{roleName, jdbcType=VARCHAR}
AND t1.status = 'deployed'
