mapper.select:
SELECT distinct t1.id AS id, t1.created_time AS createdTime, t1.oper AS operator, t1.status AS status, t1.proc_inst_key AS procInstKey, t1.proc_def_name AS procDefName, t1.proc_def_id AS procDefId, t2.entity_data_id AS entityDataId, t2.entity_type_id AS entityTypeId, t2.entity_data_name AS entityDataName FROM core_ru_proc_inst_info t1 LEFT JOIN core_ru_proc_exec_binding t2 ON t1.id = t2.proc_inst_id AND t2.bind_type = 'process' JOIN core_ru_proc_role_binding t3 ON t1.proc_def_id = t3.proc_id AND t3.role_name IN
#{roleName, jdbcType=VARCHAR}
AND t3.permission = 'USE' ORDER BY t1.created_time DESC LIMIT 500

SELECT distinct t1.id AS id, t1.created_time AS createdTime, t1.oper AS operator, t1.status AS status, t1.proc_inst_key AS procInstKey, t1.proc_def_name AS procDefName, t1.proc_def_id AS procDefId, t2.entity_data_id AS entityDataId, t2.entity_type_id AS entityTypeId, t2.entity_data_name AS entityDataName FROM core_ru_proc_inst_info t1 LEFT JOIN core_ru_proc_exec_binding t2 ON t1.id = t2.proc_inst_id AND t2.bind_type = 'process' JOIN core_ru_proc_role_binding t3 ON t1.proc_def_id = t3.proc_id AND t3.role_name IN
#{roleName, jdbcType=VARCHAR}
AND t3.permission = 'USE'
AND t1.created_time >= #{startTime,jdbcType=TIMESTAMP}
AND t1.created_time <= #{endTime,jdbcType=TIMESTAMP}
AND t2.entity_data_name like #{entityDataName,jdbcType=VARCHAR}
AND t1.proc_def_name like #{procInstName,jdbcType=VARCHAR}
AND t1.oper like #{operator,jdbcType=VARCHAR}
AND t1.status = #{status,jdbcType=VARCHAR}
ORDER BY t1.created_time DESC

select
count(1)
from core_ru_proc_inst_info
where proc_def_id =
#{procDefId,jdbcType=VARCHAR}
and
status =
#{status,jdbcType=VARCHAR}

AND created_time >= #{startDate,jdbcType=TIMESTAMP}


AND created_time <= #{endDate,jdbcType=TIMESTAMP}

select

from core_ru_proc_inst_info
where proc_def_id =
#{procDefId,jdbcType=VARCHAR}
and
status =
#{status,jdbcType=VARCHAR}

AND created_time >= #{startDate,jdbcType=TIMESTAMP}


AND created_time <= #{endDate,jdbcType=TIMESTAMP}

SELECT DISTINCT t0.proc_def_id AS procDefId, t0.proc_def_name AS procDefName, IFNULL(tmp1.totalInstances, 0) AS totalInstances, IFNULL(tmp2.totalInProgressInstances, 0) AS totalInProgressInstances, IFNULL(tmp3.totalCompletedInstances, 0) AS totalCompletedInstances, IFNULL(tmp4.totalFaultedInstances, 0) AS totalFaultedInstances FROM core_ru_proc_inst_info t0 LEFT JOIN (SELECT proc_def_id, COUNT(1) AS totalInstances FROM core_ru_proc_inst_info WHERE 1=1
AND created_time >= #{startDate,jdbcType=TIMESTAMP}
AND created_time <= #{endDate,jdbcType=TIMESTAMP}
AND proc_def_id IN
#{procDefId, jdbcType=VARCHAR}
GROUP BY proc_def_id) tmp1 ON tmp1.proc_def_id = t0.proc_def_id LEFT JOIN (SELECT proc_def_id, COUNT(1) AS totalInProgressInstances FROM core_ru_proc_inst_info WHERE status = 'InProgress'
AND created_time >= #{startDate,jdbcType=TIMESTAMP}
AND created_time <= #{endDate,jdbcType=TIMESTAMP}
AND proc_def_id IN
#{procDefId, jdbcType=VARCHAR}
GROUP BY proc_def_id) tmp2 ON tmp2.proc_def_id = t0.proc_def_id LEFT JOIN (SELECT proc_def_id, COUNT(1) AS totalCompletedInstances FROM core_ru_proc_inst_info WHERE status = 'Completed'
AND created_time >= #{startDate,jdbcType=TIMESTAMP}
AND created_time <= #{endDate,jdbcType=TIMESTAMP}
AND proc_def_id IN
#{procDefId, jdbcType=VARCHAR}
GROUP BY proc_def_id) tmp3 ON tmp3.proc_def_id = t0.proc_def_id LEFT JOIN (SELECT proc_def_id, COUNT(1) AS totalFaultedInstances FROM core_ru_proc_inst_info WHERE status = 'InternallyTerminated'
AND created_time >= #{startDate,jdbcType=TIMESTAMP}
AND created_time <= #{endDate,jdbcType=TIMESTAMP}
AND proc_def_id IN
#{procDefId, jdbcType=VARCHAR}
GROUP BY proc_def_id) tmp4 ON tmp4.proc_def_id = t0.proc_def_id WHERE 1 = 1
AND t0.created_time >= #{startDate,jdbcType=TIMESTAMP}
AND t0.created_time <= #{endDate,jdbcType=TIMESTAMP}
AND t0.proc_def_id IN
#{procDefId, jdbcType=VARCHAR}
ORDER BY ${sortField} ${sortType}

select
count(1)
from core_ru_proc_inst_info
where 1=1
and proc_def_name =
#{procDefName,jdbcType=VARCHAR}

AND status =#{status,jdbcType=VARCHAR}

and 
proc_batch_key =
#{procBatchKey,jdbcType=VARCHAR}

select
from core_ru_proc_inst_info where 1=1 and proc_def_name = #{procDefName,jdbcType=VARCHAR}
AND status =#{status,jdbcType=VARCHAR}
and proc_batch_key = #{procBatchKey,jdbcType=VARCHAR} order by proc_def_name LIMIT 500
