mapper.select:
SELECT distinct t1.id AS id, t1.created_time AS createdTime, t1.oper AS operator, t1.status AS status, t1.proc_inst_key AS procInstKey, t1.proc_def_name AS procDefName, t1.proc_def_id AS procDefId, t2.entity_data_id AS entityDataId, t2.entity_type_id AS entityTypeId, t2.entity_data_name AS entityDataName FROM core_ru_proc_inst_info t1 LEFT JOIN core_ru_proc_exec_binding t2 ON t1.id = t2.proc_inst_id AND t2.bind_type = 'process' JOIN core_ru_proc_role_binding t3 ON t1.proc_def_id = t3.proc_id AND t3.role_name IN
#{roleName, jdbcType=VARCHAR}
AND t3.permission = 'USE' ORDER BY t1.created_time DESC LIMIT 500
